// src/App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

/* Components */
import Features from './components/Features';
import Contact from './components/Contact';
import Menu from './components/Menu';
import AuthPage from './components/AuthPage';  // Import the AuthPage

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true;

const client = axios.create({
  baseURL: "http://127.0.0.1:8000"
});

function App() {
  const [currentUser, setCurrentUser] = useState();

  useEffect(() => {
    client.get("/api/user")
      .then(function(res) {
        setCurrentUser(true);
      })
      .catch(function(error) {
        setCurrentUser(false);
      });
  }, []);

  function submitLogout(e) {
    e.preventDefault();
    client.post("/api/logout", { withCredentials: true })
      .then(function(res) {
        setCurrentUser(false);
      });
  }

  if (currentUser) {
    return (
      <div className='header'>
        <Navbar className='navbar-custom'>
          <Container>
            <Navbar.Brand>My projecct</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Navbar.Text>
                <form onSubmit={e => submitLogout(e)}>
                  <Button className='logout-button'  type="submit" variant="light">Log out</Button>
                </form>
              </Navbar.Text>
            </Navbar.Collapse>
          </Container>
        </Navbar>
        <div className="button-container">
          <button className="button support">Support</button>
          <button className="button community">Community</button>
          <Link to="/features" className="button features">Features</Link>
          <Link to="/Contact" className="button contact-us"> Contact Us</Link>
          <button className="button language">English</button>
          <button className="button search-button">
            <FontAwesomeIcon icon={faSearch} />
          </button>
        </div>
        <Menu />
        <div className="home">
          <img src="/media/pexels-googledeepmind-17485633.jpg" alt="Sample" />
        </div>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={<AuthPage setCurrentUser={setCurrentUser} />} />
        <Route path="/features" element={<Features />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </Router>
  );
}

export default App;
