// src/components/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const client = axios.create({
  baseURL: "http://127.0.0.1:8000"
});

function AuthPage({ setCurrentUser }) {
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isRegistering ? "/api/register" : "/api/login";
    const data = isRegistering ? { email, username, password } : { email, password };

    try {
      const response = await client.post(endpoint, data);
      
      if (isRegistering) {
        // After successful registration, switch to login mode
        setIsRegistering(false);
        // Optionally, you can automatically fill the login fields
        setUsername(username); // Assuming username is required for login
      } else {
        // After successful login, set current user and redirect
        setCurrentUser(true);
        navigate('/'); // Redirect to home page
      }
    } catch (error) {
      console.error('Authentication failed', error);
    }
  };

  return (
    <div className="auth-page">
      <Form onSubmit={handleSubmit}>
        {isRegistering && (
          <Form.Group className="mb-3" controlId="formBasicUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" placeholder="Enter username" value={username} onChange={e => setUsername(e.target.value)} />
          </Form.Group>
        )}
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" value={email} onChange={e => setEmail(e.target.value)} />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        </Form.Group>
        <Button variant="primary" type="submit">
          {isRegistering ? "Register" : "Login"}
        </Button>
        <Button variant="link" onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Already have an account? Login" : "Don't have an account? Register"}
        </Button>
      </Form>
    </div>
  );
}

export default AuthPage;
