// src/components/LoginPage.js
import './AuthPage.css';
import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const client = axios.create({
  baseURL: "http://192.168.1.1:8000"
});

function LoginPage({ setCurrentUser }) {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // State for error messages

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await client.post("/api/login", { email, password });
      setCurrentUser(true);
      navigate('/'); // Redirect to home page
      setErrorMessage(''); // Clear any previous error message
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setErrorMessage('Wrong email address or password');
      } else {
        setErrorMessage('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div className="auth-page">
      <Form onSubmit={handleSubmit}>
        {errorMessage && <p className="text-danger">{errorMessage}</p>} {/* Display error message */}
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" value={email} onChange={e => setEmail(e.target.value)} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        </Form.Group>
        <Button variant="primary" type="submit">Login</Button>
        <Button variant="link" onClick={() => navigate('/register')}>
          Don't have an account? Register
        </Button>
      </Form>
    </div>
  );
}

export default LoginPage;
