import './App.css';
import React, { useState, useEffect } from 'react';
import client from './axiosConfig';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Lottie from 'lottie-react';
import animationData from './assets/media/animation/main.json';
import secondAnimation from './assets/media/animation/Animation - 1721222788256.json';

/* Components */
import Features from './components/Features';
import Contact from './components/Contact';
import Register from './components/Register';
import Login from './components/Login';
import Header from './components/Header';
import CurrentHeader from './components/CurrentHeader';

function App({ setCurrentUser }) {
  const [currentUser, setCurrentUserState] = useState();

  useEffect(() => {
    client.get("/api/user")
      .then(res => {
        setCurrentUserState(true);
      })
      .catch(error => {
        console.error('Error fetching user', error);
        setCurrentUserState(false);
      });
  }, []);

  const submitLogout = (e) => {
    e.preventDefault();
    client.post('/api/logout', {})
      .then(res => {
        setCurrentUserState(false);
        setCurrentUser(false);
      })
      .catch(error => {
        console.error('Error logging out:', error);
      });
  };

  if (currentUser) {
    return (
      <div className='current-user-header'>
        <CurrentHeader submitLogout={submitLogout} />
        <div className="current-main-page">

        <Lottie className='animation'
          animationData ={animationData} 
          loop={true} 
          autoplay={true} 
           />
           </div>
      </div>
    );
  }

  return (
    <div className="main-page">
      <div className='animation-container'>
        <div className='animation-container-text'>
          <h1><b>Enhance Your<br /> Analytics with AI</b></h1>
          <h3>Solve problems at the new speed of business with a <br /> trusted analytics platform.</h3>
          <div className='animation-container-button'>
            <button className='button learn-more'>Learn More</button>
            <button className='button watch-now'>Watch Now</button>
          </div>
        </div>
      </div>
      <Header />
    </div>
  );
}

function RoutesConfig() {
  const [currentUser, setCurrentUser] = useState(false);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<App setCurrentUser={setCurrentUser} />} />
        <Route path="/register" element={<Register setCurrentUser={setCurrentUser} />} />
        <Route path="/login" element={<Login setCurrentUser={setCurrentUser} />} />
        <Route path="/features" element={<Features />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </Router>
  );
}

export default RoutesConfig;
