import axios from 'axios';

// Set default configurations for Axios
axios.defaults.xsrfCookieName = 'csrftoken'; // Default name of CSRF cookie
axios.defaults.xsrfHeaderName = 'X-CSRFToken'; // Default name of CSRF header
axios.defaults.withCredentials = true; // Include cookies in requests

// Create an Axios instance with a base URL
const newclient = axios.create({
  baseURL: "http://127.0.0.1:8000", // Base URL for API requests
  // Additional configurations can be added here if needed
});

export default newclient;
