// src/authPage.js
import axios from 'axios';
import Cookies from 'js-cookie'; // Ensure you have js-cookie library installed

// Create an Axios instance with a base URL
const newclient = axios.create({
  baseURL: "http://127.0.0.1:8000", // Base URL for API requests
  withCredentials: true, // Include cookies in requests
  headers: {
    'X-CSRFToken': Cookies.get('EPpft6wkuWtcRYSD6vF7SlGLnoZY7nFs') || '', // Set CSRF token from cookies
  }
});

export default newclient;
